import { useState, useEffect } from "react";
import { useParams, useLocation } from "wouter";
import { useQuery } from "@tanstack/react-query";
import { Share2 } from "lucide-react";

interface DemoSlide {
  id: string;
  type: 'intro' | 'video' | 'callout';
  title?: string;
  content?: string;
  videoUrl?: string;
  duration?: number;
  order: number;
}

interface DemoPresentation {
  id: string;
  title: string;
  slug: string;
  description?: string;
  slides: DemoSlide[];
}

export default function Demo() {
  const params = useParams();
  const [, navigate] = useLocation();
  const slug = params.slug || 'idig-demo';

  const [currentSlide, setCurrentSlide] = useState(0);
  const [isFullscreen, setIsFullscreen] = useState(false);
  const [isPlaying, setIsPlaying] = useState(false);

  // Check if this is a shared link visit
  const urlParams = new URLSearchParams(window.location.search);
  const isSharedView = urlParams.has('shared');

  // Mock data for different demos
  const getDemoPresentation = (slug: string): DemoPresentation => {
    switch (slug) {
      case 'idig-lens':
        return {
          id: '2',
          title: 'i<em>DIG</em> Lens Demo',
          slug: 'idig-lens',
          description: 'Highlight, save, and explore insights',
          slides: [
            {
              id: '1',
              type: 'video',
              title: 'i<em>DIG</em> Lens Demo',
              videoUrl: 'https://f004.backblazeb2.com/file/idigdemo/idig_demo_final.mp4',
              order: 1
            }
          ]
        };
      case 'idig-feeds':
        return {
          id: '3',
          title: 'i<em>DIG</em> Feeds',
          slug: 'idig-feeds',
          description: 'Turn searches into discovery engines',
          slides: [
            {
              id: '1',
              type: 'intro',
              title: 'i<em>DIG</em> Feeds',
              content: 'Convert your search queries into intelligent discovery feeds. Track topics of interest and receive curated content that deepens your understanding over time.',
              order: 1
            }
          ]
        };
      case 'idig-matching':
        return {
          id: '4',
          title: 'i<em>DIG</em> Matching',
          slug: 'idig-matching',
          description: 'AI-powered skill and experience matching',
          slides: [
            {
              id: '1',
              type: 'intro',
              title: 'i<em>DIG</em> Matching',
              content: 'Intelligent matching system that connects your skills, interests, and learning goals with relevant opportunities, people, and resources across the web.',
              order: 1
            }
          ]
        };
      case 'idig-scenes':
        return {
          id: '5',
          title: 'i<em>DIG</em> Scenes',
          slug: 'idig-scenes',
          description: 'Curate rich experiences around ideas',
          slides: [
            {
              id: '1',
              type: 'intro',
              title: 'i<em>DIG</em> Scenes',
              content: 'Create immersive, contextual environments around your ideas. Organize related content, conversations, and insights into rich, explorable scenes.',
              order: 1
            }
          ]
        };
      default: // idig-demo
        return {
          id: '1',
          title: 'i<em>DIG</em> Demo',
          slug: 'idig-demo',
          description: 'Interactive demonstration of i<em>DIG</em> capabilities',
          slides: [
            {
              id: '1',
              type: 'intro',
              title: 'i<em>DIG</em> (Deep Inquiry Generator)',
              content: 'i<em>DIG</em> (Deep Inquiry Generator) is a chrome extension in development. The following presentation is for demonstration purposes only.\n\nI will add more text later.',
              order: 1
            },
            {
              id: '2', 
              type: 'video',
              title: 'i<em>DIG</em> Movable Menu',
              videoUrl: '/objects/uploads/idig-movable-menu.mp4', // Will be updated with actual path
              order: 2
            }
          ]
        };
    }
  };

  const mockPresentation = getDemoPresentation(slug);

  const { data: presentation } = useQuery({
    queryKey: ['/api/presentations', slug],
    queryFn: () => Promise.resolve(mockPresentation), // Will be replaced with actual API
    enabled: !!slug,
  });

  const handleNext = () => {
    if (presentation && currentSlide < presentation.slides.length - 1) {
      setCurrentSlide(currentSlide + 1);
    }
  };

  const handlePrevious = () => {
    if (currentSlide > 0) {
      setCurrentSlide(currentSlide - 1);
    }
  };

  const toggleFullscreen = () => {
    if (!document.fullscreenElement) {
      document.documentElement.requestFullscreen();
      setIsFullscreen(true);
    } else {
      document.exitFullscreen();
      setIsFullscreen(false);
    }
  };

  const exitToHomepage = () => {
    navigate('/');
    // Small delay to ensure navigation completes before scrolling
    setTimeout(() => {
      const demoSection = document.querySelector('[data-testid="demo-section"]');
      if (demoSection) {
        demoSection.scrollIntoView({ 
          behavior: 'smooth', 
          block: 'center' 
        });
      }
    }, 100);
  };

  const shareVideo = async () => {
    if (presentation) {
      // Create the demo presentation URL with shared parameter
      const demoUrl = `${window.location.origin}/demo/${presentation.slug}?shared=true`;

      try {
        if (navigator.share) {
          // Use native share API if available
          await navigator.share({
            title: `${presentation.title} - i<em>DIG</em> Demo`,
            text: `Check out this interactive demo: ${presentation.title}`,
            url: demoUrl,
          });
        } else {
          // Fallback to clipboard
          await navigator.clipboard.writeText(demoUrl);
          console.log('Demo link copied to clipboard!');
        }
      } catch (error) {
        // Fallback for older browsers
        const textArea = document.createElement('textarea');
        textArea.value = demoUrl;
        document.body.appendChild(textArea);
        textArea.select();
        document.execCommand('copy');
        document.body.removeChild(textArea);
        console.log('Demo link copied to clipboard!');
      }
    }
  };

  const handleKeyPress = (e: KeyboardEvent) => {
    switch (e.key) {
      case 'ArrowRight':
      case ' ':
        e.preventDefault();
        handleNext();
        break;
      case 'ArrowLeft':
        e.preventDefault();
        handlePrevious();
        break;
      case 'f':
      case 'F':
        e.preventDefault();
        toggleFullscreen();
        break;
      case 'Escape':
        if (isFullscreen) {
          setIsFullscreen(false);
        }
        break;
    }
  };

  useEffect(() => {
    document.addEventListener('keydown', handleKeyPress);
    return () => document.removeEventListener('keydown', handleKeyPress);
  }, [currentSlide, isFullscreen]);

  useEffect(() => {
    const handleFullscreenChange = () => {
      setIsFullscreen(!!document.fullscreenElement);
    };

    document.addEventListener('fullscreenchange', handleFullscreenChange);
    return () => document.removeEventListener('fullscreenchange', handleFullscreenChange);
  }, []);

  if (!presentation) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <h1 className="text-2xl font-medium mb-4">Loading presentation...</h1>
        </div>
      </div>
    );
  }

  const currentSlideData = presentation.slides[currentSlide];
  const isLastSlide = currentSlide === presentation.slides.length - 1;
  const isFirstSlide = currentSlide === 0;

  return (
    <div className={`${isFullscreen ? 'fixed inset-0 z-50' : 'min-h-screen'} ${currentSlideData?.type === 'video' ? 'bg-transparent' : 'bg-black'} text-white flex flex-col`}>
      {/* Header Controls */}
      {!isFullscreen && (
        <div className="bg-gray-900 p-4 flex items-center justify-between">
          <button
            onClick={exitToHomepage}
            className="text-blue-400 hover:text-blue-300 transition-colors"
          >
            â† Back to i<em>DIG</em>
          </button>
          <h1 className="text-xl font-medium">{presentation.title}</h1>
          <div className="flex items-center space-x-3">
            {currentSlideData?.type === 'video' && (
              <button
                onClick={shareVideo}
                className="bg-blue-600 hover:bg-blue-700 px-4 py-2 rounded-lg transition-colors flex items-center space-x-2"
                title="Share video"
              >
                <Share2 size={16} />
                <span>Share</span>
              </button>
            )}
            <button
              onClick={toggleFullscreen}
              className="bg-blue-600 hover:bg-blue-700 px-4 py-2 rounded-lg transition-colors"
            >
              Fullscreen
            </button>
          </div>
        </div>
      )}

      {/* Main Content Area */}
      <div className={`flex-1 ${currentSlideData?.type === 'video' ? 'flex' : 'flex items-center justify-center'} relative`}>
        {currentSlideData.type === 'intro' && (
          <div className="max-w-4xl mx-auto px-8 text-center">
            <h2 className="text-5xl md:text-7xl font-light mb-8 leading-tight">
              {currentSlideData.title}
            </h2>
            <div className="text-xl md:text-2xl text-gray-300 leading-relaxed space-y-6">
              {currentSlideData.content?.split('\n\n').map((paragraph, index) => (
                <p key={index}>{paragraph}</p>
              ))}
            </div>
          </div>
        )}

        {currentSlideData.type === 'video' && (
          <div className="w-full h-full flex items-center justify-center p-4">
            <div className="relative w-full max-w-6xl">
              <video
                className="w-full h-auto max-h-[80vh] object-contain rounded-lg"
                controls
                autoPlay={true}
                preload="metadata"
                onPlay={() => setIsPlaying(true)}
                onPause={() => setIsPlaying(false)}
              >
                <source src={currentSlideData.videoUrl} type="video/mp4" />
                Your browser does not support the video tag.
              </video>

              {/* Prominent Play Demo Button Overlay - Only for shared links */}
              {!isPlaying && isSharedView && (
                <div className="absolute inset-0 flex items-center justify-center pointer-events-none">
                  <div className="bg-blue-600 hover:bg-blue-700 text-white px-8 py-4 rounded-full shadow-lg transition-all hover:scale-105 pointer-events-auto cursor-pointer flex items-center space-x-3 text-lg font-medium"
                       onClick={() => {
                         const video = document.querySelector('video');
                         if (video) video.play();
                       }}>
                    <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 24 24">
                      <path d="M8 5v14l11-7z"/>
                    </svg>
                    <span>Play Demo</span>
                  </div>
                </div>
              )}
            </div>
          </div>
        )}

        {currentSlideData.type === 'callout' && (
          <div className="max-w-4xl mx-auto px-8 text-center">
            <h2 className="text-4xl md:text-6xl font-light mb-8">
              {currentSlideData.title}
            </h2>
            <div className="text-xl md:text-2xl text-gray-300 leading-relaxed">
              {currentSlideData.content}
            </div>
          </div>
        )}
      </div>

      {/* Bottom Controls - Hidden for single slide presentations */}
      {presentation.slides.length > 1 && (
        <div className="p-6 flex items-center justify-between bg-gray-900/50">
        <button
          onClick={handlePrevious}
          disabled={isFirstSlide}
          className={`px-6 py-2 rounded-lg transition-colors ${
            isFirstSlide 
              ? 'bg-gray-700 text-gray-500 cursor-not-allowed' 
              : 'bg-blue-600 hover:bg-blue-700 text-white'
          }`}
        >
          Previous
        </button>

        <div className="flex items-center space-x-4">
          <span className="text-gray-300">
            {currentSlide + 1} / {presentation.slides.length}
          </span>
          <div className="flex space-x-1">
            {presentation.slides.map((_, index) => (
              <div
                key={index}
                className={`w-2 h-2 rounded-full transition-colors ${
                  index === currentSlide ? 'bg-blue-400' : 'bg-gray-600'
                }`}
              />
            ))}
          </div>
        </div>

        <button
          onClick={isLastSlide ? exitToHomepage : handleNext}
          className="bg-blue-600 hover:bg-blue-700 px-6 py-2 rounded-lg transition-colors text-white"
        >
          {isLastSlide ? 'Finish' : 'Next'}
        </button>
      </div>
      )}

      {/* Keyboard shortcuts hint */}
      {isFullscreen && (
        <div className="absolute top-4 left-4 text-sm text-gray-400 bg-black/50 p-3 rounded-lg">
          <div>â† â†’ Navigate slides</div>
          <div>Space: Next slide</div>
          <div>F: Fullscreen</div>
          <div>Esc: Exit fullscreen</div>
        </div>
      )}
    </div>
  );
}

